class ProgramCategoryPhoto < ActiveRecord::Base
  	belongs_to :category_program

    has_attachment :content_type => :image,
                   :storage => :s3,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
                   :processor    =>  :MiniMagick
    validates_as_attachment
end